mapper.select:
SELECT count(1) as count, permission, componentId FROM (SELECT g.name as name, group_role.role as permission, group_role.resource_id as componentId FROM groups g INNER JOIN group_roles group_role ON group_role.group_id=g.id UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, group_role.role as permission, group_role.resource_id as componentId FROM group_roles group_role where group_role.group_id IS NULL ) groups where groups.componentId in
#{id}
GROUP BY groups.permission, groups.componentId

SELECT count(1) FROM (SELECT g.name as name FROM groups g INNER JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name FROM group_roles group_role where group_role.role=#{permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups

select count(1)
from (
  select distinct gr.group_id
  ) g

mapper.sql:
from ( select g.id as group_id, g.name as name, gr.role as permission, gr.resource_id as resource_id, gr.id as id from groups g left join group_roles gr on g.id = gr.group_id UNION ALL select 0 as group_id, 'Anyone' as name, gr.role as permission, gr.resource_id as resource_id, gr.id as id from group_roles gr
where gr.group_id is null
) gr left join projects p on gr.resource_id = p.id
and lower(gr.name) like #{query.searchQueryToSql} ESCAPE '/'
and gr.permission is not null
and gr.resource_id is null
and p.uuid=#{query.componentUuid}
and gr.permission=#{query.permission}

mapper.select:
SELECT sub.id, sub.groupId, sub.resourceId, sub.role FROM ( SELECT gr.id AS id, gr.group_id AS groupId, gr.resource_id AS resourceId, gr.role AS role, g.name AS name FROM group_roles gr INNER JOIN groups g ON g.id=gr.group_id UNION ALL SELECT gr.id as id, 0 AS group_id, gr.resource_id AS resource_id, gr.role AS role, 'Anyone' AS name FROM group_roles gr WHERE gr.group_id is null ) sub where sub.name IN
#{name}
AND sub.resourceId=#{projectId}
AND sub.resourceId IS NULL
